            log('报告,当前要删除的评论id', comment_id)
            apiCommentDelete(comment_id, function(r){
                var delete_comment = JSON.parse(r)
                comment_form = e('#comment-id-'+comment_id)
                // log('报告,我要移除的评论标签:', comment_form)
                comment_form.remove()
            })
        }
    })
}


